import Layout from '../common/Layout';
import axios from 'axios';
//데이터를 가지고 와야 되기 때문
import { useEffect, useState, useRef } from 'react';
//컴포넌트가 마운트 됐을 때 의존성 배열을 비워서 axios를 호출하기 위해 useEffect필요
//axios로 받은 데이터가 state에 옮겨 담아져야지 리턴문을 만들수 있기 때문에 useState 필요
function Gallery() {
	const key = 'ca74b70b0051720330ff8c2bef418cbf';
	const method_interest = 'flickr.interestingness.getList';
	const num = 20;
	const url = `https://www.flickr.com/services/rest/?method=${method_interest}&per_page=${num}&api_key=${key}&format=json&nojsoncallback=1`;

	const [Item, setItem] = useState([]);

	useEffect(() => {
		axios.get(url).then((json) => {
			console.log(json);
		});
	});

	return (
		<Layout name={'Gallery'}>
			<div className='frame'></div>
		</Layout>
	);
}

export default Gallery;
