import Layout from '../common/Layout';
import axios from 'axios';
import { useEffect, useState } from 'react';

function Youtube() {
	const info = [
		{
			img: `${process.env.PUBLIC_URL}/img/youtube_thumbnails1.jpg`,
		},
		{
			img: `${process.env.PUBLIC_URL}/img/youtube_thumbnails2.jpg`,
		},
		{
			img: `${process.env.PUBLIC_URL}/img/youtube_thumbnails3.jpg`,
		},
		{
			img: `${process.env.PUBLIC_URL}/img/youtube_thumbnails4.jpg`,
		},
		{
			img: `${process.env.PUBLIC_URL}/img/youtube_thumbnails5.jpg`,
		},
		{
			img: `${process.env.PUBLIC_URL}/img/youtube_thumbnails6.jpg`,
		},
	];
	const [Vids, setVids] = useState([]);

	// const [Index, setIndex] = useState(0);

	useEffect(() => {
		const key = 'AIzaSyB07SmnE1cJMVJRKCZmg_kaotLyw9z0IiE';
		const playlist = 'PL4LRIdOIlx__4Jk4ktxkUK2l6bEDSp5ZU';
		const num = 6;

		const url = `https://www.googleapis.com/youtube/v3/playlistItems?part=snippet&key=${key}&playlistId=${playlist}&maxResults=${num}`;
		axios.get(url).then((json) => {
			console.log(json.data.items);
			setVids(json.data.items);
		});
	}, []);

	return (
		<Layout name={'Youtube'}>
			{Vids.map((vid, idx) => {
				const tit = vid.snippet.title;
				const desc = vid.snippet.description;
				const date = vid.snippet.publishedAt;
				const thumbnails = `${process.env.PUBLIC_URL}/img/youtube_thumbnails`;
				const num = vid.snippet.position;
				return (
					<article key={idx}>
						<div className='pic'>
							<img src={`${thumbnails}${num}`} alt={vid.snippet.title} />
						</div>
						<h3>{tit.length > 30 ? tit.substr(0, 30) + '...' : tit}</h3>
						<div className='txt'>
							<p>{desc.length > 150 ? desc.substr(0, 150) + '...' : desc}</p>
							<span>{date.split('T')[0]}</span>
						</div>
					</article>
				);
			})}
		</Layout>
	);
}

export default Youtube;
