import { Route } from 'react-router-dom';

//common
import Header from './components/common/Header';
import Footer from './components/common/Footer';
// import Layout from './components/common/Layout';

//main
import Visual from './components/main/Visual';
import News from './components/main/News';
import Section_coffee from './components/main/Section_coffee';
import Section_dessert from './components/main/Section_dessert';
import Section_shop from './components/main/Section_shop';

//sub
import Members from './components/sub/Members';

import './scss/style.scss';
function App() {
	return (
		<>
			{/* <Switch>
				<Route exact path='/'>
					<Header type={'main'} />
					<Visual />
				</Route>

				<Route path='/'>
					<Header type={'sub'} />
				</Route>
			</Switch>
			<Route path='/members' component={Members} />

			<Footer /> */}

			{/* 헤더와 푸터는 경로가 없기 때문에 모든 페이지에서 다 보여질 것*/}
			<Header />

			{/* path:경로 , '/':보여준다는 뜻 
			Route라는 방에 경로를 만들어 보여준다고 생각하면 됨*/}

			<Route exact path='/'>
				<Visual />
				<News />
				<Section_coffee />
				<Section_dessert />
				<Section_shop />
			</Route>

			{/* members 페이지로 가도 메인페이지 영역들이 보이는 이유는 /가 들어있기 때문에 /도 보여주고 경로가 members인 애도 보여주는 것*/}
			{/* 그래서 exact를 넣어 줘야 됨*/}
			<Route path='/members' component={Members} />

			{/* <Route path='/members'>
				<Members />
			</Route> */}

			<Footer />
		</>
	);
}

export default App;
