import Layout from '../common/Layout';
import { useEffect, useState, useRef } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import Masonry from 'react-masonry-component';
import { fetchFlickr } from '../../redux/flickrSlice';

function Gallery() {
	const dispatch = useDispatch();
	const frame = useRef(null);
	const pop = useRef(null);
	const input = useRef(null);
	const flickr = useSelector((store) => store.flickr.data);
	const [Opt, setOpt] = useState({ type: 'user', user: '195940766@N07' });
	const [Index, setIndex] = useState(0);
	const [Loading, setLoading] = useState(true);
	const [EnableClick, setEnableClick] = useState(true);
	const masonryOptions = { transitionDuration: '0.5s' };

	const [Open, setOpen] = useState(false);

	const startLoading = () => {
		setLoading(true);
		frame.current.classList.remove('on');
	};

	const endLoading = () => {
		setTimeout(() => {
			frame.current.classList.add('on');
			setLoading(false);
			setTimeout(() => setEnableClick(true), 1000);
		}, 1000);
	};

	const showInterest = () => {
		if (!EnableClick) return;
		setEnableClick(false);
		startLoading();
		setOpt({ type: 'interest' });
	};

	const showSearch = () => {
		if (!EnableClick) return;
		setEnableClick(false);
		const result = input.current.value.trim();
		input.current.value = '';
		if (!result) return alert('Please enter a search word.');
		startLoading();

		setOpt({
			type: 'search',
			tags: result,
		});
	};

	const showUser = (e) => {
		if (!EnableClick) return;
		setEnableClick(false);

		let userID = '195940766@N07';
		e.target.innerText !== 'My Gallery' && (userID = e.target.innerText);
		startLoading();
		setOpt({ type: 'user', user: userID });
	};

	const openPop = (index) => {
		pop.current.open();
		setIndex(index);
	};

	useEffect(() => {
		dispatch(fetchFlickr(Opt));
	}, [Opt]);

	useEffect(endLoading, [flickr]);

	return (
		<>
			<Layout name={'Gallery'}>
				<div className='title'>
					<h2>CATALOG</h2>
				</div>
				{Loading && (
					<img
						className='loading'
						src={`${process.env.PUBLIC_URL}/img/loading.gif`}
					/>
				)}
				<div className='btn'>
					<button onClick={showInterest}>Interest Gallery</button>
					<button onClick={showUser}>My Gallery</button>
				</div>{' '}
				<div className='searchBox'>
					<input
						type='text'
						ref={input}
						placeholder='Please enter a search word.'
						onKeyUp={(e) => {
							if (e.key === 'Enter') showSearch();
						}}
					/>
					<button onClick={showSearch}>SEARCH</button>
				</div>
				<div className='frame' ref={frame}>
					<Masonry elementType={'div'} options={masonryOptions}>
						{flickr.map((pic, idx) => {
							return (
								<article key={idx}>
									<div className='flickr'>
										<div className='pic' onClick={() => openPop(idx)}>
											<img
												src={`https://live.staticflickr.com/${pic.server}/${pic.id}_${pic.secret}_m.jpg`}
												alt={pic.title}
											/>
										</div>
										<h2>{pic.title}</h2>
										<div className='profile'>
											<img
												src={`http://farm${pic.farm}.staticflickr.com/${pic.server}/buddyicons/${pic.owner}.jpg`}
												alt={pic.owner}
												onError={(e) => {
													e.target.setAttribute(
														'src',
														'https://www.flickr.com/images/buddyicon.gif'
													);
												}}
											/>
											<span onClick={showUser}>{pic.owner}</span>
										</div>
									</div>
								</article>
							);
						})}
					</Masonry>
				</div>
			</Layout>

			{/* <Popup ref={pop}>
				{flickr.length !== 0 && (
					<img
						src={`https://live.staticflickr.com/${flickr[Index].server}/${flickr[Index].id}_${flickr[Index].secret}_b.jpg`}
						alt={flickr[Index].title}
					/>
				)}
			</Popup> */}
			{Open && (
				<Popup setOpen={setOpen}>
					<img
						src={`https://live.staticflickr.com/${pic[Index].server}/${pic[Index].id}_${pic[Index].secret}_b.jpg`}
						alt={pic[Index].title}
					/>
				</Popup>
			)}
		</>
	);
}

export default Gallery;
