import Layout from '../common/Layout';
import { useRef, useEffect, useState } from 'react';

function Location() {
	const { kakao } = window;
	const container = useRef(null);
	//useEffect에서 만들어진 지도 인스턴스를 담을 state 생성

	const [Location, setLocation] = useState(null);

	const option = {
		center: new kakao.maps.LatLng(33.450701, 126.570667),
		level: 3,
	};

	const imageSrc = `${process.env.PUBLIC_URL}/img/coffee.png`;
	const imageSize = new kakao.maps.Size(428, 494);
	const imageOption = { offset: new kakao.maps.Point(214, 247) };

	const markerImage = new kakao.maps.MarkerImage(
		imageSrc,
		imageSize,
		imageOption
	);

	const markerPosition = new kakao.maps.LatLng(33.450701, 126.570667);
	const marker = new kakao.maps.Marker({
		position: markerPosition,
		image: markerImage,
	});

	useEffect(() => {
		const map_instance = new kakao.maps.Map(container.current, option);
		marker.setMap(map_instance);
		setLocation(map_instance);
		//맵 인스턴스를 컴포넌트에서 자유롭게 쓰기 위해 state에 저장
	}, []);

	return (
		<Layout name={'Location'}>
			<div id='map' ref={container}></div>

			<button
				onClick={() => {
					Location.addOverlayMapTypeId(kakao.maps.MapTypeId.TRAFFIC);
				}}>
				Traffic ON
			</button>
			<button
				onClick={() => {
					Location.removeOverlayMapTypeId(kakao.maps.MapTypeId.TRAFFIC);
				}}>
				Traffic OFF
			</button>
		</Layout>
	);
}

export default Location;
