import Layout from '../common/Layout';
import { useRef, useEffect, useState } from 'react';
//Post라는 state를 만들어서 Post state가 바뀔 때마다 콘솔로 확인해야 작업하기 편할 테니 useEffect도 사용

function Community() {
	//먼저 input이라는 textarea를 참조객체로 참조해야 됨
	const input = useRef(null);
	const textarea = useRef(null);

	//이제 input과 textarea에 입력한 값을 저장해야 되기 때문에 state도 만들어야 됨
	const [Posts, setPosts] = useState([]);
	//각각의 객체들이 배열로 담길 예정이니 초기값은 []빈배열

	return (
		<Layout name={'Community'}>
			<div className='inputBox'>
				<input type='text' placeholder='제목을 입력하세요' ref={input} />
				{/* 이렇게 하면 input 객체 안에 있는 current key 값에 이 input요소가 참조가 될 것 */}
				<br />
				{/* useRef로 참조해서 글 작성 버튼을 누르는 순간에 useRef로 참조된 input요소와 textarea에 각각에 글을 가지고 와서 저장하는 식으로*/}
				<textarea
					cols='30'
					rows='5'
					placeholder='본문을 입력하세요 '
					ref={textarea}></textarea>
				{/* 이렇게 하면 textarea 객체 안에 있는 current key 값에 이 textarea요소가 참조가 될 것 */}
				<br />

				<button>CANCEL</button>
				<button>WRITE</button>
				{/* 이렇게 입력한 값을 Post(배열로 만들 것)라는 state를 만들어서 각각의 데이터를 객체로 만들어서 넣을 예정, 담겨진 값의 형태가 객체인 것뿐 */}
			</div>
		</Layout>
	);
}

export default Community;
