import Layout from '../common/Layout';
import { useRef, useEffect, useState } from 'react';

function Community() {
	const dummyPosts = [
		{ title: '1', content: 'a' },
		{ title: '2', content: 'b' },
		{ title: '3', content: 'c' },
		{ title: '4', content: 'd' },
		{ title: '5', content: 'e' },
	];

	const input = useRef(null);
	const textarea = useRef(null);

	const [Posts, setPosts] = useState(dummyPosts);

	const resetForm = () => {
		input.current.value = '';
		textarea.current.value = '';
	};

	const createPost = () => {
		if (!input.current.value.trim() || !textarea.current.value.trim()) {
			resetForm();
			return alert('제목과 본문을 모두 입력하세요');
		}
		setPosts([
			...Posts,
			{ title: input.current.value, content: textarea.current.value },
		]);
		resetForm();
	};

	const deletePost = (index) => {
		console.log(index);

		setPosts(Posts.filter((post, idx) => idx !== index));
	};

	//글 수정모드 변경함수
	//수정을 원하는 글에 edit 버튼을 누르면 바로 수정하는 게 아니라
	//이 글을 수정 가능한 상태로 만들기
	//순번을 알아야 하기 때문에 index파라미터
	const enableUpdate = (index) => {
		//수정 버튼을 클릭하면 idx값이 enableUpdate함수 안에 index 파라미터로넘어오게 될 것
		//Posts라는 state의 배열로 각각의 객체값이 저장되어 있는데
		//그 객체값에서는 enableUpdate라는 key값을 추가해서 그 값을 true라고 지정
		//state가 변경이 되어 다음번 사이클이 동작이 될 때 맵이 enableUpdate라는 key값이 있으면 출력 모드가 아니라 수정 모드의 상태로 화면에 나타내 주기

		setPosts(
			Posts.map((post, idx) => {
				//idx와 파라미터로 넘어온 순번이 만약에 같으면
				//반복하는 post에 enableUpdate라는 key값을 추가하고
				//true라는 값을 지정한 다음 return으로 내보내면 됨
				if (idx === index) post.enableUpdate = true;
				return post;
			})
		);
	};

	useEffect(() => {
		console.log(Posts);
	}, [Posts]);

	return (
		<Layout name={'Community'}>
			<div className='inputBox'>
				<input type='text' placeholder='제목을 입력하세요' ref={input} />
				<br />
				<textarea
					cols='30'
					rows='5'
					placeholder='본문을 입력하세요 '
					ref={textarea}></textarea>
				<br />

				<button onClick={resetForm}>CANCEL</button>
				<button onClick={createPost}>WRITE</button>
			</div>

			<div className='showBox'>
				{Posts.map((post, idx) => {
					return (
						<article key={idx}>
							<h2>{post.title}</h2>
							<p>{post.content}</p>

							<div className='btnSet'>
								<button onClick={() => enableUpdate(idx)}>EDIT</button>
								<button onClick={() => deletePost(idx)}>DELELTE</button>
							</div>
						</article>
					);
				})}
			</div>
		</Layout>
	);
}

export default Community;
