import Layout from '../common/Layout';
import axios from 'axios';
import { useEffect, useState } from 'react';

function Member() {
	const path = process.env.PUBLIC_URL;
	const [Members, setMembers] = useState([]);

	useEffect(() => {
		axios.get(`${path}/DB/members.json`).then((json) => {
			setMembers(json.data.members);
			//data를 받아서 console만 찍으면 의미가 없고 이 데이터를 어딘가에 담아서 담겨있는 값에 따라서 화면에 랜더링이 바뀌도록 처리해야 되기 때문에
			//화면에 랜더링을 관장하는 중요한 정보값은 state로 담으면 됨
			//여기까지 하면 Members라는 state 값에 이 data가 확인될 것
			//초기값에는 Members안에 빈 배열값이 들어가 있으니까
			//이 Members에 데이터가 담기는 순간에 Members state 값이 값을잘 품고 있는지 확인해 보자
		});
	}, []);

	useEffect(() => [], [Members]);

	return (
		<Layout name={'Members'}>
			<p>members</p>
		</Layout>
	);
}

export default Member;
