import Layout from '../common/Layout';
import { useRef, useEffect, useState } from 'react';

function Community() {
	const dummyPosts = [
		{ title: '1', content: 'a' },
		{ title: '2', content: 'b' },
		{ title: '3', content: 'c' },
		{ title: '4', content: 'd' },
		{ title: '5', content: 'e' },
	];

	const input = useRef(null);
	const textarea = useRef(null);

	const [Posts, setPosts] = useState(dummyPosts);

	const resetForm = () => {
		input.current.value = '';
		textarea.current.value = '';
	};

	const createPost = () => {
		if (!input.current.value.trim() || !textarea.current.value.trim()) {
			resetForm();
			return alert('제목과 본문을 모두 입력하세요');
		}
		setPosts([
			...Posts,
			{ title: input.current.value, content: textarea.current.value },
		]);
		resetForm();
	};

	const deletePost = (index) => {
		console.log(index);

		setPosts(Posts.filter((post, idx) => idx !== index));
	};

	const enableUpdate = (index) => {
		setPosts(
			Posts.map((post, idx) => {
				if (idx === index) post.enableUpdate = true;
				return post;
			})
		);
	};

	useEffect(() => {
		console.log(Posts);
	}, [Posts]);

	return (
		<Layout name={'Community'}>
			<div className='inputBox'>
				<input type='text' placeholder='제목을 입력하세요' ref={input} />
				<br />
				<textarea
					cols='30'
					rows='5'
					placeholder='본문을 입력하세요 '
					ref={textarea}></textarea>
				<br />

				<button onClick={resetForm}>CANCEL</button>
				<button onClick={createPost}>WRITE</button>
			</div>

			<div className='showBox'>
				{Posts.map((post, idx) => {
					return (
						<article key={idx}>
							{/* post.enableUpdate = true;값이 true면
              return문으로 반복출력할 때 분기처리,
              enableUpdate가 true인 post는 input요소로 감싸서 수정 가능하게 하고
              그렇지 않다고 하면 원래 그대로 출력하도록 */}
							{/* 그래서 수정모드와 출력모드를 분기처리 해야 함 */}
							{/* 위에 것까지는 똑같고 여기서부터 달라지기 때문에 여기에 분기처리 */}
							{post.enableUpdate ? <>수정모드</> : <>출력모드</>}
							<h2>{post.title}</h2>
							<p>{post.content}</p>

							<div className='btnSet'>
								<button onClick={() => enableUpdate(idx)}>EDIT</button>
								<button onClick={() => deletePost(idx)}>DELELTE</button>
							</div>
						</article>
					);
				})}
			</div>
		</Layout>
	);
}

export default Community;
