import Layout from '../common/Layout';
import { useRef, useEffect, useState } from 'react';

function Community() {
	us;

	return (
		<Layout name={'Community'}>
			<div className='inputBox'>
				<input type='text' placeholder='제목을 입력하세요' />
				<br />
				{/* useRef로 참조해서 글 작성 버튼을 누르는 순간에 useRef로 참조된 input요소와 textarea에 각각에 글을 가지고 와서 저장하는 식으로*/}
				<textarea cols='30' rows='5' placeholder='본문을 입력하세요'></textarea>
				<br />

				<button>CANCEL</button>
				<button>WRITE</button>
				{/* 이렇게 입력한 값을 Post(배열로 만들 것)라는 state를 만들어서 각각의 데이터를 객체로 만들어서 넣을 예정, 담겨진 값의 형태가 객체인 것뿐 */}
			</div>
		</Layout>
	);
}

export default Community;
