import React from 'react';
import Layout from '../common/Layout';
import axios from 'axios';
//데이터를 가지고 와야 되기 때문
import { useEffect, useState, useRef } from 'react';
//컴포넌트가 마운트 됐을 때 의존성 배열을 비워서 axios를 호출하기 위해 useEffect필요
//axios로 받은 데이터가 state에 옮겨 담아져야지 리턴문을 만들수 있기 때문에 useState 필요
function Gallery() {
	const frame = useRef(null);
	const [Items, setItems] = useState([]);

	const getFlickr = () => {
		const key = 'fc209af65c0a7e52e3ce3a2358041834';
		// const key = '4612601b324a2fe5a1f5f7402bf8d87a';

		const method_interest = 'flickr.interestingness.getList';
		const num = 20;
		const url = `https://www.flickr.com/services/rest/?method=${method_interest}&per_page=${num}&api_key=${key}&format=json&nojsoncallback=1`;

		axios.get(url).then((json) => {
			console.log(json.data.photos.photo);
			setItems(json.data.photos.photo);
		});
	};

	useEffect(() => {
		getFlickr();
	}, []);

	return (
		<Layout name={'Gallery'}>
			<div className='frame on' ref={frame}>
				{Items.map((item, idx) => {
					return (
						<article key={idx}>
							<div className='inner'>
								<div className='pic'>
									<img
										src={`https://live.staticflickr.com/${item.server}/${item.id}_${item.secret}_m.jpg`}
										alt={item.title}
									/>
								</div>
								<h2>{item.title}</h2>
							</div>
						</article>
					);
				})}
			</div>
		</Layout>
	);
}

export default Gallery;
