import Layout from '../common/Layout';
import { useRef, useEffect, useState } from 'react';

function Location() {
	const { kakao } = window;
	const info = [
		{
			title: '언더프레셔 삼성 본점',
			latlng: new kakao.maps.LatLng(37.51415882109348, 127.06407736804755),
			imgUrl: `${process.env.PUBLIC_URL}/img/coffee.png`,
			imgSize: new kakao.maps.Size(428, 494),
			imgPos: { offset: new kakao.maps.Point(214, 247) },
		},
		{
			title: '삼성동 코엑스',
			latlng: new kakao.maps.LatLng(37.5126964707762, 127.06070547684942),
			imgUrl: `${process.env.PUBLIC_URL}/img/coffee.png`,
			imgSize: new kakao.maps.Size(428, 494),
			imgPos: { offset: new kakao.maps.Point(214, 247) },
		},
		{
			title: '소전서림(유료 도서관)',
			latlng: new kakao.maps.LatLng(37.52364138030701, 127.05620912952872),
			imgUrl: `${process.env.PUBLIC_URL}/img/coffee.png`,
			imgSize: new kakao.maps.Size(428, 494),
			imgPos: { offset: new kakao.maps.Point(214, 247) },
		},
	];
	const container = useRef(null);
	const [Location, setLocation] = useState(null);
	const [Traffic, setTraffic] = useState(false);
	const [Info, setInfo] = useState(info);
	const [Index, setIndex] = useState(0);
	// Info[]안에 들어갈 숫자반 바뀌면 위치가 바뀌는 걸 확인 했으니
	//버튼을 세 개 만들고 버튼을 누룰 때마다 출력할 데이터의 순번값이 Index라는 state공간에 저장이 되어야 한다
	//2. 버튼을 클릭할 때마다 버튼의 순서값을 Index라는 state안에 담아야 한다

	const option = {
		center: Info[0].latlng,
		level: 3,
	};

	const markerPosition = Info[0].latlng;
	const imageSrc = Info[0].imgUrl;
	const imageSize = Info[0].imgSize;
	const imageOption = Info[0].imgPos;

	const markerImage = new kakao.maps.MarkerImage(
		imageSrc,
		imageSize,
		imageOption
	);

	const marker = new kakao.maps.Marker({
		position: markerPosition,
		image: markerImage,
	});

	useEffect(() => {
		const map_instance = new kakao.maps.Map(container.current, option);
		marker.setMap(map_instance);
		setLocation(map_instance);
	}, []);

	useEffect(() => {
		if (!Location) return;
		Traffic
			? Location.addOverlayMapTypeId(kakao.maps.MapTypeId.TRAFFIC)
			: Location.removeOverlayMapTypeId(kakao.maps.MapTypeId.TRAFFIC);
	}, [Traffic]);

	return (
		<Layout name={'Location'}>
			<div id='map' ref={container}></div>
			<button onClick={() => setTraffic(!Traffic)}>
				{Traffic ? 'Traffic OFF' : 'Traffic On'}
			</button>

			{/* 1.일단 버튼을 만들어야 한다*/}
			<ul className='branch'>
				<li onClick={() => setIndex(0)}>언더프레셔</li>
				<li onClick={() => setIndex(1)}>삼성동 코엑스</li>
				<li onClick={() => setIndex(2)}>소전서림</li>
			</ul>
		</Layout>
	);
}

export default Location;
